{"filter":false,"title":"Manager.php","tooltip":"/class/Manager.php","undoManager":{"mark":30,"position":30,"stack":[[{"start":{"row":0,"column":0},"end":{"row":188,"column":2},"action":"insert","lines":["<?php","class ManagerRessource{","// ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++","    // caratéristique","// ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++","    protected $_conn;","// °°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°","    // CONSTRUCT","// °°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°","    public function __construct($conn){$this->setDb($conn);}    ","// ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++","// fonctionnalité","// ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++","","","    public function RealAdd(Array $nom)","    {    ","        // ajoute une ressource de type Real","        foreach($nom as $val) {","            if(!empty($val)){","                $q = $this->_conn->prepare('INSERT INTO ressource(type, nom) VALUES(\"real\", :nom)');","                $q->bindValue(':nom', $val);","                $q->execute();","            }","        };","   ","    }","// °°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°","    public function OnlineAdd(string $nom, string $url)","    {    ","        // ajoute une ressource de type online","            if(!empty($nom) && !empty($url)){","                $q = $this->_conn->prepare('INSERT INTO ressource(type, nom, url) VALUES(\"online\", :nom, :url)');","                $q->bindValue(':nom', $nom);","                $q->bindValue(':url', $url);","                $q->execute();","            }","    }","// °°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°","    public function Select(string $type)","    {","        $content = [];","        // selection toute les ressources","        if($type === \"online\"){","            $update = [];","            // select all field where last_jour != today","            $q = $this->_conn->prepare('SELECT * FROM  ressource WHERE type = \"online\" AND last_jour != :last_jour');","            $q->execute(['last_jour' => $this->getToday()]);","            while ($donnees = $q->fetch(PDO::FETCH_ASSOC))","            {","                $update[] = $donnees;","            }","            if(!empty($update)){","                foreach ($update as $key => $value) {","                // update them  ","                $q = $this->_conn->prepare('UPDATE `ressource` SET `last_jour` = :last_jour, nbr_jour = :nbr_jour WHERE `id` = :id');","                $q->bindValue(':last_jour', $this->getToday());","                $q->bindValue(':nbr_jour', $this->getNbrJour($value['last_jour'], $value['nbr_jour']));","                $q->bindValue(':id', (int)$value['id']);","                $q->execute();","                }","            }","","        }","        $q = $this->_conn->prepare('SELECT * FROM  ressource WHERE type = :type');","        $q->bindValue(':type', $type);","        $q->execute();","","        while ($donnees = $q->fetch(PDO::FETCH_ASSOC))","        {","            $content[] = $donnees;","        }","","","        return json_encode($content);","","    }","// °°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°°","    public function del(int $id)","    {","    // supprime une ressource","        $res = null;","        try {","            $q = $this->_conn->prepare('DELETE FROM ressource WHERE id = :id');","            $q->execute(['id' => $id]);","            $res[] = \"succes\";     ","        } catch (\\Throwable $th) {","            throw $th;","            $res[] = \"error\";","            return json_encode($res);","        }","        return json_encode($res)